// filemanager
/**
 * is non-markdown file
 * @param assetPath
 * @returns
 */
const isAsset = assetPath => /.(js|css|scss|njk|ejs|png|jpe?g|gif|svg|webp|json|html|txt)$/.test(String(assetPath));
/**
 * is markdown file
 * @param markdownPath
 * @returns
 */
const isMarkdown = markdownPath => /.(md)$/i.test(String(markdownPath));

export { isAsset, isMarkdown };
